package modalclasses;

public class Passenger {

	private long ticketNo;
	private String passengerName;
	private short passengerAge;
	private String passengerGender;
	private String passengerBerthPreference;

	public Passenger() {
		// TODO Auto-generated constructor stub
	}

	public long getTicketNo() {
		return ticketNo;
	}

	public void setTicketNo(long ticketNo) {
		this.ticketNo = ticketNo;
	}

	public String getPassengerName() {
		return passengerName;
	}

	public void setPassengerName(String passengerName) {
		this.passengerName = passengerName;
	}

	public short getPassengerAge() {
		return passengerAge;
	}

	public void setPassengerAge(short passengerAge) {
		this.passengerAge = passengerAge;
	}

	public String getPassengerGender() {
		return passengerGender;
	}

	public void setPassengerGender(String passengerGender) {
		this.passengerGender = passengerGender;
	}

	public String getPassengerBerthPreference() {
		return passengerBerthPreference;
	}

	public void setPassengerBerthPreference(String passengerBerthPreference) {
		this.passengerBerthPreference = passengerBerthPreference;
	}

}
